mapper.select:
select
  id, name, parent_id, created_at, updated_at, deleted_at
from category


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from category


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}
